const initialState = { 
  topics: {
      topics: {
        '123': {
          id: '123',
          name: 'example topic',
          icon: 'icon url',
          quizIds: ['456']
        }
      }
  }
};

const topicsSlice = createSlice({
  name: 'topics/addTopic',
  initialState: initialState,
  reducers: {
    addTopics: (state, action) => { 
      state.topics.push(action.payload) 
    }        
  },
})
