import { createSlice } from '@reduxjs/toolkit'; 

const initialState = { 
  topics: {
      topics: {
        '123': {
          id: '123',
          name: 'example topic',
          icon: 'icon url',
          quizIds: ['456']
        }
      }
  }
};

export const topicsSlice = createSlice({
  name: 'topics/addTopic',
  initialState: initialState,
  reducers: {
    addTopics: (state, action) => { 
      state.topics.push(action.payload) 
    }        
  }, 
})

const selectAllTopics = createSelector(
  topicsSlice.getSelectors()[0],
  (topics) => topics.topics
)

export const selectTopicById = createSelector(
  selectAllTopics,
  (topics) => topics['123']
)
